            otv.append(otv[i - 1 - x[i]] + 1)
            ma = max(ma, otv[i - 1 - x[i]] + 1)
        else:
            otv.append(1)
            ma = max(ma, 1)
    min_destroyed = n - ma
    return min_destroyed

