import heapq

def minimum_travel_costs(n, m, roads):
    inf = 10000000001.0
    e = {i: [] for i in range(n)}
    for (v, u, w) in roads:
        e[v - 1].append((u - 1, w))
        e[u - 1].append((v - 1, w))
    d = [inf] * n
    d[0] = 0
    q = []
    heapq.heappush(q, (0, 0))
    while q:
        (vd, v) = heapq.heappop(q)
        l = []
        for (u, w) in e[v]:
            td = [0] * n
            td[u] = w
            l.append(u)
        for u in l:
            tdu = td[u]
            for (x, w) in e[u]:
                cv = vd + (tdu + w) ** 2
                if cv < d[x]:
                    d[x] = cv
                    heapq.heappush(q, (cv, x))
    for i in range(n):
        if d[i] == inf:
            d[i] = -1
    return d

