        if x in b:
            l = b[x][0]
            r = b[x][1]
            dfs(l)
            if mn[x - 1] > mn[l - 1]:
                mn[x - 1] = mn[l - 1]
            if mx[x - 1] < mx[l - 1]:
                mx[x - 1] = mx[l - 1]
            dfs(r)
            if mn[x - 1] > mn[r - 1]:
                mn[x - 1] = mn[r - 1]
            if mx[x - 1] < mx[r - 1]:
                mx[x - 1] = mx[r - 1]
        else:
            return
    results = []
    for case in test_cases:
        n = case['n']
        edges = case['edges']
        b = {}
        mn = [10000000] * n
        mx = [0] * n
        ans = 0
        for j in range(n):
            (y, z) = edges[j]
            if y == -1:
                mn[j] = z
                mx[j] = z
            else:
                b[j + 1] = [y, z]
        dfs(1)
        for j in range(1, n + 1):
            if j in b:
                l = b[j][0]
                r = b[j][1]
                if mx[l - 1] < mn[r - 1]:
                    continue
                elif mx[r - 1] < mn[l - 1]:
                    ans += 0
                else:
                    ans = -1
                    break
        results.append(ans)
    return results

