import re
import math

def convert_recipe_to_grams(recipe: str) -> str:

    def repl(m):
        quantity = eval(m.group(1))
        unit = m.group(2)
        ratio = 15 if unit == 'tbsp' else 5
        grams = math.ceil(quantity * ratio)
        return f'{m.group(0)} ({grams}g)'
    return re.sub('', repl, recipe)

