def count_lucky_numbers_in_factorial(n, lucky_numbers=[4, 7]):

    def fact(n):
        if n < 2:
            return 1
        else:
            return n * fact(n - 1)
    factorial_value = fact(n)
    factorial_str = list(str(factorial_value))
    count = 1
    for char in factorial_str:
        if int(char) in lucky_numbers:
            count += 1
    return count

