def check_road_status(x1, y1, x2, y2, r):
    num = abs((x2 - x1) * y1 - x1 * (y2 - y1))
    den = math.sqrt((x2 - x1) ** 2 + (y2 - y1) ** 2)
    d = num * den
    if d == r:
        return 'JUST MISSED'
    elif d < r:
        return 'REPLANNING'
    else:
        return 'SAFE'

