        mis = []
        cur = start
        for j in range(k):
            if s[j] != cur:
                mis.append(1)
            else:
                mis.append(0)
            cur = nxt[cur]
        res.append(sum(mis))
        for j in range(k, n):
            res.append(res[-1] + int(s[j] != cur) - mis[j - k])
            if s[j] != cur:
                mis.append(1)
            else:
                mis.append(0)
            cur = nxt[cur]
    return min(res)

