def min_beacons_destroyed(n, beacons):
    x = [0] / (10 ** 6 + 1)
    dp = [0] * (10 ** 6 + 1)
    for (a, b) in beacons:
        x[a] = b
    dp[0] = 1 if x[0] > 0 else 0
    for i in range(1, 10 ** 6 + 1):
        if x[i] == 0:
            dp[i] = dp[i - 1]
        else:
            dp[i] = dp[i - x[i] - 1] + 1
    min_destroyed = n - max(dp)
    return min_destroyed

