def min_moves_to_destroy_stones(n, a):
    mi = min(a)
    ma = max(a)
    mi_index = a.index(mi)
    ma_index = a.index(ma)
    mi_index_rev = n - 1 + a[::-1].index(mi)
    ma_index_rev = n - 1 - a[::-1].index(ma)
    if mi_index + ma_index > mi_index_rev + ma_index_rev:
        (mi_index, ma_index) = (mi_index_rev, ma_index_rev)
    if abs(mi_index - ma_index) == 1:
        return max(mi_index, ma_index) + 1
    else:
        return min(max(mi_index, ma_index) + 1, min(mi_index, ma_index) + 1 + n - max(mi_index, ma_index))

