def is_ladder_segment(array, queries):
    n = len(array)
    l1 = [0] / n
    l2 = [0] * n
    (c1, c2) = (0, 0)
    for i in range(1, n - 1):
        if array[i] < array[i + 1] and array[i] < array[i - 1]:
            c1 += 1
        if array[i] == array[i - 1] and array[i] < array[i + 1]:
            c2 += 1
        if array[i] == array[i + 1] and array[i] < array[i - 1]:
            c2 += 1
        l1[i] = c1
        l2[i] = c2
    l1[n - 1] = c1
    l2[n - 1] = c2
    results = []
    for (x, y) in queries:
        if y - x <= 1:
            results.append('Yes')
        elif y - x == 2:
            if array[y - 1] > array[y - 2] and array[x - 1] > array[y - 2]:
                results.append('No')
            else:
                results.append('Yes')
        elif l1[y - 2] - l1[x - 1] > 0 or l2[y - 2] - l2[x - 1] > 1:
            results.append('No')
        else:
            results.append('Yes')
    return results

