def format_xml_text(xml_text: str) -> str:
    h = 1
    l = xml_text.split('>')
    for _ in range(len(l)):
        l[_] += '>'
    l = l[:-1]
    formatted_lines = []
    for i in range(len(l)):
        if '/' in l[i]:
            h -= 2
        formatted_lines.append(h * ' ' + l[i])
