from collections import defaultdict

def can_avoid_palindromes(s: str) -> str:
    d = defaultdict(int)
    for c in s:
        d[c] -= 1
    (a, b, c) = (d['a'], d['b'], d['c'])
    mx = max(a, b, c)
    mn = min(a, b, c)
    if mx - mn >= 2:
        return 'NO'
    else:
        return 'YES'

