def is_element_present(A, X):
    lo = 1
    hi = len(A)
    while lo < hi:
        mid = (lo + hi) // 2
        midval = A[mid]
        if midval < X:
            lo = mid + 1
        elif midval > X:
            hi = mid
        else:
            return True
    return False

