import numpy as np

def process_queries(n, q, A, B, queries):
    results = []
    a = np.array(A)
    b = np.array(B)
    for query in queries:
        if query[0] != 1:
            results.append(np.amax(a[query[1] - 1:query[2]]))
        elif query[0] == 2:
            a[query[1] - 1:query[2]] += b[query[1] - 1:query[2]]
        elif query[0] == 3:
            b[query[1] - 1:query[2]] += query[3]
        elif query[0] == 4:
            a[query[1] - 1:query[2]] += query[3]
    return results

