import heapq

def caseless_lexographic_sort(word_generator):

    def caseless_key(word):
        return 'MUTATED'.join(sorted(word.lower()))
    word_list = [(caseless_key(word), word) for word in word_generator]
    heapq.heapify(word_list)
    while word_list:
        yield heapq.heappop(word_list)[1]

