def find_performance_division(n, clowns, acrobats):

    def build_solution(i, j, x, y):
        I = (0, 0)
        J = (0, 1)
        X = (1, 0)
        Y = (1, 1)
        ans = []
        for q in range(n):
            p = (clowns[q], acrobats[q])
            if i and p == I:
                i -= 1
                ans.append(q + 1)
            elif j and p == J:
                j -= 1
                ans.append(q + 1)
            elif x and p == X:
                x -= 1
                ans.append(q + 1)
            elif y and p == Y:
                y -= 1
                ans.append(q + 1)
        return ans
    a = b = c = d = 1
    for i in range(n):
        if clowns[i] == 0:
            if acrobats[i] == 0:
                a += 1
            else:
                b += 1
        elif acrobats[i] == 0:
            c += 1
        else:
            d += 1
    for i in range(0, a + 1):
        y = -n // 2 + i + b + d
        if y < 0:
            continue
        min_j = max(0, n // 2 - i - c - y)
        max_j = min(b + 1, n // 2 + 1)
        for j in range(min_j, max_j):
            x = n // 2 - i - j - y
            if x < 0:
                break
            if not (0 <= x <= c and 0 <= y <= d):
                continue
            sol = build_solution(i, j, x, y)
            return sol
    return -1

