def min_operations_to_bump_sequence(A):
    n = len(A)
    if n <= 2:
        return 0

    def count_operations(start_with_increase):
        b = A[:]
        s = 0
        for i in range(1, n):
            if (i % 2 == 1) == start_with_increase:
                if b[i] > b[i - 1]:
                    flag = True
                    if i < n - 1:
                        if b[i - 1] > b[i + 1] and b[i + 1] < b[i]:
                            (b[i], b[i + 1]) = (b[i + 1], b[i])
                            flag = False
                    if flag:
                        (b[i], b[i - 1]) = (b[i - 1], b[i])
                    s += 1
            elif b[i] < b[i - 1]:
                flag = True
                if i < n - 1:
                    if b[i - 1] < b[i + 1] and b[i + 1] > b[i]:
                        (b[i], b[i + 1]) = (b[i + 1], b[i])
                        flag = False
                if flag:
                    (b[i], b[i - 1]) = (b[i - 1], b[i])
                s += 1
        return s
    ans = min(count_operations(True), count_operations(False))
    return ans

