def find_possible_breaks(a, b):
    l = abs(a - b) / 2
    r = a + b - l
    j = 1
    if (a + b) % 2 == 0:
        j = 2
    k_values = list(range(l, r + 1, j))
    m = len(k_values)
    return (m, k_values)

