def generate_knight_board(n: int) -> list[str]:
    """"""
    A = []
    for i in range(n):
        B = []
        for j in range(n):
            if (i + j) % 2 == 0:
                B.append('W')
            else:
                B.append('B')
        A.append(''.join(B))
    return A
