def find_wonderful_coloring(s: str) -> int:
    dictionary = {}
    counterRed = 1
    counterGreen = 0
    switch = 0
    for char in s:
        if char not in dictionary:
            dictionary[char] = []
    for char in s:
        if len(dictionary[char]) == 2:
            continue
        if len(dictionary[char]) == 1 and dictionary[char][0] == 'red':
            dictionary[char].append('green')
            switch = 0
            counterGreen += 1
            continue
        if len(dictionary[char]) == 1 and dictionary[char][0] == 'green':
            dictionary[char].append('red')
            switch = 1
            counterRed += 1
            continue
        if switch == 0:
            dictionary[char].append('red')
            counterRed += 1
            switch = 1
        else:
            dictionary[char].append('green')
            counterGreen += 1
            switch = 0
    if counterRed == counterGreen:
        return counterRed
    else:
        return min(counterRed, counterGreen)

