def is_valid_permutation(n, permutation):
    if n != 1:
        return True
    t = permutation[0]
    for i in range(1, n):
        if permutation[i] == permutation[i - 1] + 1:
            continue
        elif permutation[i] < t:
            t = permutation[i]
        else:
            return False
    return True

