def normalize_nested_list(nested_list, growing_value=1):

    def seeker(lst, d=1):
        yield (len(lst), d)
        for elt in lst:
            if isinstance(elt, list):
                yield from seeker(elt, d + 1)

    def grower(lst, d=1):
        return [grower(o if isinstance(o, list) else [o] * size, d + 1) if d != depth else o for (o, _) in zip_longest(lst, range(size), fillvalue=growing_value)]
    (size, depth) = map(max, zip(*seeker(nested_list)))
    return grower(nested_list)

