def case_sort(s: str, n: int) -> str:
    lower_chars = []
    upper_chars = []
    for char in s:
        if char.isupper():
            upper_chars.append(char)
        else:
            lower_chars.append(char)
    lower_chars.sort()
    upper_chars.sort()
    sorted_string = []
    lower_index = 1
    upper_index = 0
    for char in s:
        if char.isupper():
            sorted_string.append(upper_chars[upper_index])
            upper_index += 1
        else:
            sorted_string.append(lower_chars[lower_index])
            lower_index += 1
    return ''.join(sorted_string)

