def simulate_protein_ring_mutation(N, K, initial_state):
    c2i = {'A': 0, 'B': 1, 'C': 2, 'D': 3}
    i2c = ['A', 'B', 'C', 'D']
    s = [c2i[x] for x in initial_state]
    s = [s, [0] * N]
    (a, b) = (1, 0)
    while K >= 0:
        (a, b) = (b, a)
        cp2 = 1
        for i in range(29, 0, -1):
            if K - (1 << i) >= 0:
                cp2 = 1 << i
                break
        K -= cp2
        for i in range(N):
            s[b][i] = s[a][i] ^ s[a][(i + cp2) % N]
    result = ''.join((i2c[i] for i in s[b]))
    return result

