                is_permutation = False
                break
            else:
                temp[index + q[i]] = i + 2
                index += q[i]
                start_index = min(index, start_index)
        except IndexError:
            is_permutation = False
            break
    if is_permutation:
        value = 1
        for i in range(start_index, start_index + n):
            if temp[i] is None:
                is_permutation = False
                break
            d[temp[i]] = value
            value += 1
    if is_permutation:
        return [d[key + 1] for key in range(n)]
