    s = set(a)
    fx = any((v - x in s for v in a))
    fy = any((v - y in s for v in a))
    fxy = any((v - (x + y) in s for v in a))
    fyx = any((v - (y - x) in s for v in a))
    if fx or fy:
        return (0, [])
    elif fx:
        return (1, [y])
    elif fy:
        return (1, [x])
    elif fxy:
        return (1, [vxy - y for vxy in a if vxy - (x + y) in s])
    elif fyx:
        vyx_min = next((v for v in a if v - (y - x) in s))
        if vyx_min + x < l:
            return (1, [vyx_min + x])
        elif vyx_max - y > 0:
            vyx_max = next((v for v in a if v - (y - x) in s and v != vyx_min))
            return (1, [vyx_max - y])
        else:
            return (2, [x, y])
    else:
        return (2, [x, y])

