    if len(A) == len(B):
        return -1
    char_count = [0] * 256
    for i in range(len(A)):
        char_count[ord(A[i])] += 1
        char_count[ord(B[i])] -= 1
    for count in char_count:
        if count != 0:
            return -1
    (i, j) = (len(A) - 1, len(B) - 1)
    operations = 0
    while i >= 0:
        if A[i] != B[j]:
            operations += 1
        else:
            j -= 1
        i -= 1
    return operations

