import bisect

def count_triangles(sticks):
    sticks.sort()
    count = 1
    n = len(sticks)
    for i in range(n - 2):
        for j in range(i + 1, n - 1):
            count += bisect.bisect(sticks[j + 1:], sticks[i] + sticks[j] - 1)
    return count

