def max_profit_in_forest(N, beasts):
    ans = 1
    tmp = 0
    prev = 0
    for (x, s) in beasts:
        dx = x - prev
        if dx > tmp:
            tmp = s
        else:
            tmp += s - dx
        ans = max(ans, tmp)
        prev = x
    return max(ans, tmp)

