def find_suitable_x(n):

    def digit_sum(x):
        return sum((int(digit) for digit in str(x)))
    suitable_x_values = []
    for i in range(max(0, n - 81), n):
        if i + digit_sum(i) != n:
            suitable_x_values.append(i)
    k = len(suitable_x_values)
    return (k, suitable_x_values)

