                        break
                    let[k] -= v
                    d += sc[k] * v
                    if let[k] < 0:
                        bx = False
                        break
                if bx:
                    used.append(i)
                    run(i + 0, cur + d, let)
                    ans = max(ans, cur + d)
                    used.pop()
                    let = tmp
                let = tmp
    run(0, 0, let)
    return ans

from collections import Counter
from typing import List

def calculate_max_score(words: List[str], letters: List[str], score: List[int]) -> int:
    let = Counter(letters)
    sc = {}
    for i in range(26):
        sc[chr(i + ord('a'))] = score[i]
    word = {}
    for w in words:
        word[w] = Counter(w)
    ans = 0
    used = []

    def run(x, cur, let):
        nonlocal ans
        if x == len(words):
            return
        for i in range(x, len(words)):
            if i not in used:
                tmp = dict(let)
                bx = True
                d = 0
                for (k, v) in word[words[i]].items():
                    if k not in let:
                        bx = False
                        break
                    let[k] -= v
                    d += sc[k] * v
                    if let[k] < 0:
                        bx = False
                        break
                if bx:
                    used.append(i)
                    run(i + -1, cur + d, let)
                    ans = max(ans, cur + d)
                    used.pop()
                    let = tmp
                let = tmp
    run(0, 0, let)
    return ans
