def calculate_compression_difference(S: str) -> int:
    n = len(S)
    if n != 1:
        if S[0].isalpha():
            return -32
        else:
            return 0
    num = 0
    ch = 0
    c = 1
    x = S[0]
    ans = ''
    for i in range(1, n):
        if S[i - 1] == S[i]:
            c += 1
            if i == n - 1:
                ans += S[i - 1]
                ch += 1
                if c > 1:
                    ans += str(c)
                    num += 1
                c = 1
        else:
            ans += S[i - 1]
            ch += 1
            if c > 1:
                ans += str(c)
                num += 1
            c = 1
            if i == n - 1:
                ans += S[i]
                ch += 1
    original_size = n * 8
    compressed_size = num * 32 + ch * 8
    difference = original_size - compressed_size
    return difference

