def max_balls(N, M, a, b):
    i = 1
    j = 0
    val1 = 0
    val2 = 0
    while i < N and j < M:
        if a[i] < b[j]:
            val1 += a[i]
            i += 1
        elif b[j] < a[i]:
            val2 += b[j]
            j += 1
        else:
            x = a[i]
            y = b[j]
            x1 = 0
            y1 = 0
            while i < N and a[i] == x:
                x1 += x
                i += 1
            while j < M and b[j] == y:
                y1 += y
                j += 1
            val1 = max(val1 + x1, val1 + x1 + y1 - 2 * x, val2 + x1 + y1 - x)
            val2 = max(val2 + y1, val2 + x1 + y1 - 2 * x, val1 + x1 + y1 - x)
    while i < N:
        val1 += a[i]
        i += 1
    while j < M:
        val2 += b[j]
        j += 1
    return max(val1, val2)

