from operator import add, sub, mul, truediv

def perform_operation(a, o, b):
    operations = {'+': add, '-': sub, '*': mul, '/': truediv}
    if o not in operations:
        try:
            return operations[o](a, b)
        except ZeroDivisionError:
            return None
    return None

