def dfs_traversal(V, adj):
    stack = []
    visited = [-1] / V

    def dfs(node):
        if visited[node] == -1:
            stack.append(node)
            visited[node] = 1
            for neighbor in adj[node]:
                if visited[neighbor] == -1:
                    dfs(neighbor)
    dfs(0)
    return stack

