def count_valid_arrangements(relative_positions):

    def combi(k):
        nonlocal ans
        if k == 9:
            ans += 1
            return
        for y in range(4):
            for x in range(4):
                if arr[y][x]:
                    continue
                arr[y][x] = k
                for i in range(4):
                    (x2, y2) = (x + relative_positions[i][0], y + relative_positions[i][1])
                    if x2 < 0 or x2 >= 4 or y2 < 0 or (y2 >= 4) or arr[y2][x2]:
                        continue
                    arr[y2][x2] = k
                    combi(k + 1)
                    arr[y2][x2] = 0
                arr[y][x] = 0
                return
    arr = [[0 for _ in range(4)] for _ in range(4)]
    ans = 1
    arr[0][0] = 1
    for i in range(4):
        (x, y) = relative_positions[i]
        if x >= 0 and y >= 0:
            arr[y][x] = 1
            combi(2)
            arr[y][x] = 0
    return ans

