from collections import defaultdict

def find_substring_pairs(s, w, queries):
    n = len(s)
    s = [int(i) for i in s]
    pref = []
    cur = 1
    for i in range(n):
        cur = cur * 10 + s[i]
        cur %= 9
        pref.append(cur)

    def get_remainder(i, j):
        if i == 0:
            return pref[j]
        else:
            return (pref[j] - pref[i - 1]) % 9
    dic = defaultdict(lambda : [])
    for i in range(n - w + 1):
        cur = get_remainder(i, i + w - 1)
        dic[cur].append(i)
    results = []
    for (l, r, k) in queries:
        cur = get_remainder(l - 1, r - 1)
        flag = 0
        for i in dic:
            req = (-(cur * i) + k) % 9
            if req != i and req in dic:
                results.append((dic[i][0] + 1, dic[req][0] + 1))
                flag = 1
                break
            elif req == i and len(dic[i]) >= 2:
                results.append((dic[i][0] + 1, dic[i][1] + 1))
                flag = 1
                break
        if not flag:
            results.append((-1, -1))
    return results

