import heapq
import collections

def minimal_cost_to_planets(n, k, s, a, alliance):
    planet = collections.defaultdict(list)
    for i in range(2, k + 1):
        planet[i] = []
    for (i, v) in enumerate(a):
        planet[v].append(i)
    heap = [(0, a[s - 1])]
    heapq.heapify(heap)
    ans = {}
    visited = set()
    while heap:
        (dist, node) = heapq.heappop(heap)
        if node in visited:
            continue
        ans[node] = dist
        visited.add(node)
        for i in planet[node]:
            for (j, cost) in enumerate(alliance[i]):
                if j + 1 not in visited and cost != -1:
                    heapq.heappush(heap, (cost + dist, j + 1))
    for i in planet:
        if i not in visited:
            ans[i] = -1
    return [ans[v] for v in a]

