from collections import defaultdict, Counter

def min_socks_to_repaint(n, m, k, colors, instructions):
    n -= 1
    cc = [0, *colors]
    l = list(range(n))
    for (a, b) in instructions:
        (u, v) = (a, b)
        while u != l[u]:
            u = l[u]
        while v != l[v]:
            v = l[v]
        if u > v:
            l[u] = v
            while a != v:
                (a, l[a]) = (l[a], v)
        elif u < v:
            l[v] = u
            while b != u:
                (b, l[b]) = (l[b], u)
    groups = defaultdict(Counter)
    for (i, g) in enumerate(l):
        if i != g:
            l[i] = g = l[g]
            groups[g][cc[i]] += 1
    for (g, v) in groups.items():
        v[cc[g]] += 1
    return sum((sum(v.values()) - v.most_common(1)[0][1] for v in groups.values()))

