from collections import Counter
from math import factorial

def nc3(n):
    a = factorial(n)
    b = factorial(n - 3)
    return a * (b * 6)

def rem(s, k):
    t = -1
    x = -1
    for i in range(len(s)):
        if s[i][0] > 2 and s[i][1] <= k:
            if s[i][0] > 3:
                ch = (nc3(s[i][0]) - nc3(s[i][0] - 1)) / s[i][1]
            else:
                ch = 1 / s[i][1]
            if t < ch:
                t = ch
                x = i
    return x

def minimize_truly_geometric_triangles(test_cases):
    results = []
    for case in test_cases:
        n = case['N']
        c = case['C']
        k = case['K']
        lines = case['lines']
        v = case['V']
        l = {}
        for (a, b, e) in lines:
            if e in l:
                l[e].append((a, b))
            else:
                l[e] = [(a, b)]
        s = []
        for i in range(1, c + 1):
            if i in l:
                s.append([len(l[i]), v[i - 1]])
        s.sort(key=lambda x: x[0], reverse=True)
        while True:
            ma = rem(s, k)
            if ma <= -1:
                break
            else:
                s[ma][0] -= 1
                k = k - s[ma][1]
        re = 0
        for i in s:
            if i[0] > 2:
                re = re + nc3(i[0])
        results.append(int(re))
    return results

