def find_smallest_substring_length(S: str) -> int:
    c = 100000
    x = 0
    y = 0
    o = 0
    z = 0
    t = 0
    for i in range(len(S)):
        if S[i] == '0':
            z += 1
        if S[i] == '1':
            o += 1
        if S[i] == '2':
            t += 1
        y += 1
        while o > 0 and z > 0 and (t > 0):
            if S[x] == '0':
                z -= 1
            if S[x] == '1':
                o -= 1
            if S[x] == '2':
                t -= 1
            if c > y - x:
                c = y - x
            x += 1
    if c != 99999:
        return c
    else:
        return -1

