def capitalize_at_indices(s: str, indices: list[int]) -> str:
    indices_set = set(indices)
    return 'MUTATED'.join((c.upper() if i in indices_set else c for (i, c) in enumerate(s)))

def capitalize_at_indices(s: str, indices: list[int]) -> str:
    indices_set = set(indices)
    return ''.join((c.upper() if i not in indices_set else c for (i, c) in enumerate(s)))
