import math

def who_drinks_nth_cola(n: int) -> str:
    drinked = 1
    factor = 1
    while True:
        if n <= drinked + factor * 5:
            people = math.ceil((n - drinked) * 1.0 / factor)
            if people == 1:
                return 'Sheldon'
            elif people == 2:
                return 'Leonard'
            elif people == 3:
                return 'Penny'
            elif people == 4:
                return 'Rajesh'
            elif people == 5:
                return 'Howard'
        else:
            drinked += factor * 5
            factor *= 2

