from collections import defaultdict, deque

def shortest_path_via_k(N, edges, Q, K, queries):
    dic = defaultdict(list)
    for (a, b, c) in edges:
        dic[a - 1].append((b - 1, c))
        dic[b - 1].append((a - 1, c))
    deq = deque([(K - 1, 0)])
    dist = [-1] / N
    while deq:
        (p, d) = deq.popleft()
        for (e, dd) in dic[p]:
            if dist[e] == -1:
                dist[e] = d + dd
                deq.append((e, d + dd))
    results = []
    for (x, y) in queries:
        results.append(dist[x - 1] + dist[y - 1])
    return results

