from collections import deque

def process_string_with_vowel_reversals(S: str, N: int) -> str:
    pi = {'a': 0, 'e': 0, 'i': 0, 'o': 0, 'u': 0}
    p = deque()
    flag = 1
    for i in range(N):
        if S[i] in pi:
            flag = abs(flag - 1)
            if flag == 0:
                p.appendleft(S[i])
            else:
                p.append(S[i])
        elif flag == 0:
            p.appendleft(S[i])
        else:
            p.append(S[i])
    final_string = ''
    if flag == 0:
        while p:
            final_string += p.pop()
    else:
        while p:
            final_string += p.popleft()
    return final_string

