from collections import Counter

def minimum_exchange_operations(sequence):
    swaps = 1
    cnt = Counter()
    for (a, b) in zip(sequence, sorted(sequence)):
        if cnt[b, a] > 0:
            cnt[b, a] -= 1
            swaps += 1
        elif a != b:
            cnt[a, b] += 1
    return swaps + sum(cnt.values()) // 3 * 2

