def calculate_max_removals(n, q, a, queries):

    class Fenwick:

        def __init__(self, size):
            self.size = size
            self.tree = [0] * (size + 1)

        def add(self, idx, val):
            idx = int(idx)
            while idx <= self.size:
                self.tree[idx] += val
                idx += idx & -idx

        def sum(self, idx):
            ret = 0
            idx = int(idx)
            while idx > 0:
                ret += self.tree[idx]
                idx -= idx & -idx
            return ret
    a = [a[i] + (i + 1) for i in range(n)]
    query = [[] for _ in range(n + 1)]
    for i in range(q):
        (x, y) = queries[i]
        (l, r) = (x, n - y - 1)
        query[r].append((l, i))
    ft = Fenwick(n + 1)
    ans = [0 for _ in range(q + 3)]
    for r in range(n):
        ob = a[r]
        if ob <= 0:
            if ft.sum(1) >= -ob:
                (low, high) = (0, r)
                while low + 1 < high:
                    mid = low + high >> 1
                    if ft.sum(mid + 1) >= -ob:
                        low = mid
                    else:
                        high = mid
                idx = high if ft.sum(high + 1) >= -ob else low
                ft.add(1, 1)
                ft.add(idx + 2, -1)
        for qr in query[r]:
            ans[qr[1]] = ft.sum(qr[0] + 1)
    return ans[:q]

