import math

def find_diverse_matrix(r, c):
    if r == 1 or c == 1:
        return 0
    matrix = [[1] * c for _ in range(r)]
    row_gcds = [c + i + 1 for i in range(r)]
    col_gcds = [i + 1 for i in range(c)]
    for i in range(r):
        for j in range(c):
            matrix[i][j] = row_gcds[i] * col_gcds[j]
    return matrix

