    def fun(a):
        if a[1] == 0:
            return a
        else:
            return (a[1], a[1], -a[2])
        return a

    def operate(x):
        ans = list(car_number)
        c = []
        for i in range(n):
            c.append((abs(x - int(car_number[i])), int(x > int(car_number[i])), i))
        c.sort(key=fun)
        cost = 0
        for i in range(k):
            cost += c[i][0]
            ans[c[i][-1]] = str(x)
        return (cost, ''.join(ans))
    min_cost = inf
    beautiful_number = car_number
    for i in range(10):
        (cost, t) = operate(i)
        if cost < min_cost:
            min_cost = cost
            beautiful_number = t
        elif cost == min_cost:
            beautiful_number = min(beautiful_number, t)
    return (min_cost, beautiful_number)

