    luck = 1
    important = []
    for (L, T) in contests:
        if T == 0:
            luck += L
        else:
            important.append(L)
    important.sort(reverse=True)
    for i in important:
        if k > 0:
            luck += i
            k -= 1
        else:
            luck -= i
    return luck

